import React from 'react';
import './Modal.css';

export default function Modal(props) {
  if (!props.promoPrompt) {
    return null;
  }

  return (
    <>
      <div className="promoprompt">
        <div className="promoprompt-inner">
          <img
            className="image"
            type="image"
            src={'./Chess_Pieces/wQ.png'}
            onMouseDown={() => props.setPromotion('q')}
            onMouseUp={() => props.setTrigger(false)}
            alt=""
          ></img>
          <img
            className="image"
            type="image"
            src={'./Chess_Pieces/wR.png'}
            onMouseDown={() => props.setPromotion('r')}
            onMouseUp={() => {
              props.setTrigger(false);
            }}
            alt=""
          ></img>
          <img
            className="image"
            type="image"
            src={'./Chess_Pieces/wB.png'}
            onMouseDown={() => props.setPromotion('b')}
            onMouseUp={() => props.setTrigger(false)}
            alt=""
          ></img>
          <img
            className="image"
            type="image"
            src={'./Chess_Pieces/wN.png'}
            onMouseDown={() => props.setPromotion('n')}
            onMouseUp={() => props.setTrigger(false)}
            alt=""
          ></img>
        </div>
      </div>
    </>
  );
}
